<script>
	import { browser } from '$app/environment';
	import { onMount } from 'svelte';
	import account from '$lib/assets/account.svg';
	import accountWhite from '$lib/assets/account_white.svg';
	export let nickname;
	let date = new Date();
	let path, logoImage, accountImage;

	let padNumber = (num, fill) => {
		var len = ('' + num).length;
		return Array(fill > len ? fill - len + 1 || 0 : 0).join(0) + num;
	};

	const week = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
	const month = [
		'Jan',
		'Feb',
		'Mar',
		'Apr',
		'May',
		'Jun',
		'Jul',
		'Aug',
		'Sep',
		'Oct',
		'Nov',
		'Dec'
	];

	$: day = week[date.getDay()];
	$: m = month[date.getMonth()];
	$: d = padNumber(date.getDate(), 2);
	$: hour = padNumber(date.getHours(), 2);
	$: min = padNumber(date.getMinutes(), 2);
	$: sec = padNumber(date.getSeconds(), 2);
	// $: millisec = date.getMilliseconds().toString().substring(0, 2);

	onMount(() => {
		if (browser) {
			const interval = setInterval(() => {
				date = new Date();
			}, 60);
		}
		if (!nickname) {
			nickname = '';
		}

		path = new URL(window.location).pathname.indexOf('/_gathering/');
		path > -1 ? (accountImage = accountWhite) : (accountImage = account);
	});
</script>

