<style>
	header {
		display: grid;
		grid-template-columns: repeat(2, 1fr);
		grid-template-rows: 1fr;
		grid-column-gap: 0px;
		grid-row-gap: 0px;
		align-items: center;
		padding: 8px 0;
	}
	.info {
		justify-self: right;
		align-items: center;
		display: flex;
	}

	.logo {
		justify-self: left;
	}
	.logo svg {
		width: 160px;
		height: 36px;
	}
	.signin {
		margin-left: 8px;
		justify-self: right;
		display: flex;
		align-items: center;
	}
	.signin span {
		margin-right: 8px;
	}
	.signin img {
		--f: 1;
		width: 32px;
		aspect-ratio: 1;
		cursor: pointer;
		border-radius: 999px 999px 999px 999px;
		border-width: 0px;
		border-style: solid;
		transform: scale(var(--f));
		transition: 0.5s;
	}
	.signin img:hover {
		--f: 1.25;
	}

	@media (max-width: 576px) {
		header {
			display: grid;
			grid-template-rows: repeat(2, 1fr);
			grid-template-columns: 1fr;
		}
		.signin,
		.info {
			justify-self: center;
		}
	}
</style>
